import { createAPI, Ref, OnValue } from 'create-api'

const api = createAPI({
  version: '/v0',
  config: {
    databaseURL: 'https://hacker-news.firebaseio.com'
  }
})

const debug = api.onServer && !!process.env.DEBUG_API

if (api.onServer) {
  warmCache()
}

function warmCache() {
  fetchItems((api.cachedIds.top || []).slice(0, 30))
  setTimeout(warmCache, 1000 * 60 * 15)
}

function fetch(child) {
  debug && console.log(`fetching ${child}...`)
  const cache = api.cachedItems

  if (cache && cache.has(child)) {
    debug && console.log(`cache hit ${child}.`)
    return Promise.resolve(cache.get(child))
  }
  return new Promise((resolve, reject) => {
    const dbRef = Ref(api, `/v0/${child}`)
    OnValue(dbRef, snapshot => {
      const val = snapshot.val()
      if (val) val.__lastUpdated = Date.now()
      cache && cache.set(child, val)
      debug && console.log(`fetched ${child}.`)
      resolve(val)
    }, reject)
  })
}

export function fetchIdsByType(type) {
  return api.cachedIds && api.cachedIds[type]
    ? Promise.resolve(api.cachedIds[type])
    : fetch(`${type}stores`)
}

export function fetchItem(id) {
  return fetch(`item/${id}`)
}

export function fetchItems(ids) {
  return Promise.all(ids.map(id => fetchItem(id)))
}

export function fetchUser(id) {
  return fetch(`user/${id}`)
}

export function watchList(type, cb) {
  let first = true
  const dbRef = Ref(api, `/v0/${type}stores`)
  const handler = snapshot => {
    if (first)  {
      first = false
    } else {
      cb(snapshot.val())
    }
  }
  OnValue(dbRef, handler)
  return () => {
  }
}

export function host(url) {
  const host = url.replace(/^https?:\/\//, '').replace(/\/.*$/, '')
  const parts = host.split('.').slice(-3)
  if (parts[0] === 'www') {
    parts.shift()
  }
  return parts.join('.')
}

export function timeAgo(time) {
  const between = Date.now() / 1000 - Number(time)
  if (between < 3600) {
    return pluralize(~~(between / 60), ' minute')
  } else if (between < 86400) {
    return pluralize(~~(between / 3600), ' hour')
  } else {
    return pluralize(~~(between / 86400), ' day')
  }
}

function pluralize(time, label) {
  if (time === 1) {
    return time + label
  }
  return time + label + 's'
}

